// The application will create a renderer using WebGL, if possible,
// with a fallback to a canvas render. It will also setup the ticker
// and the root stage PIXI.Container
let maxSize =
  window.innerWidth < window.innerHeight
    ? window.innerWidth
    : window.innerHeight;

const STAGE_WIDTH = 700;
const STAGE_HEIGHT = 700;
const STAGE_OFFSET = 200;
const APP_OPTIONS = {
  width: STAGE_WIDTH + STAGE_OFFSET,
  height: STAGE_HEIGHT,
  antialias: true
};
const resolutionMultiplier = 20;

const app = new PIXI.Application(APP_OPTIONS);

// Environmental Variables
const horizontalPositions = [];
const verticalPositions = [];
const playerWidth = STAGE_WIDTH / resolutionMultiplier;
const playerHeight = STAGE_HEIGHT / resolutionMultiplier;
const posters = [];
let posterSpawnAmount = 1;
let posterSpawnInterval = 1500;
let posterScore = 0;
let protestInfluence = 0;
let burnValue = 198;
let burnValueMax = 198;
let pauseState = false;
let inWater = false;
const maxSpawnedPosters = 50;
const waterZoneSize = playerWidth * 4;
const waterZoneLocations = [
  { x: 20, y: 20 },
  { x: STAGE_WIDTH - waterZoneSize - 20, y: 20 },
  { x: 20, y: STAGE_HEIGHT - waterZoneSize - 20 },
  { x: STAGE_WIDTH - waterZoneSize - 20, y: STAGE_HEIGHT - waterZoneSize - 20 }
];
for (let p = 0; p < 20; p++) {
  horizontalPositions.push((STAGE_WIDTH * p) / 20);
  verticalPositions.push((APP_OPTIONS.height * p) / 20);
}

console.log({ horizontalPositions, verticalPositions });

// The application will create a canvas element for you that you
// can then insert into the DOM
document.body.appendChild(app.view);
document.body.style.display = "flex";
document.body.style.height = "100vh";
document.body.style.justifyContent = "center";
document.body.style.alignItems = "center";
document.body.style.backgroundColor = "#0A0A0A";
document.body.style.margin = "0";
document.body.style.padding = "0";

// Texts
const posterScoreText = new PIXI.Text(`Posters Burned: ${posterScore}`, {
  fontFamily: "Arial",
  fontSize: 24,
  fill: 0xff1010,
});
// posterScoreText.anchor.x = 0.5;
posterScoreText.width = 200;
posterScoreText.y = 20;
posterScoreText.x = STAGE_WIDTH;

const protestInfluenceText = new PIXI.Text(`Influence: ${protestInfluence}`, {
  fontFamily: "Arial",
  fontSize: 24,
  fill: 0xff1010,
  align: "center"
});
// protestInfluenceText.anchor.x = 0.5;
protestInfluenceText.y = 80;
protestInfluenceText.x = STAGE_WIDTH;

const burnTimerText = new PIXI.Text(`Influence: ${protestInfluence}`, {
  fontFamily: "Arial",
  fontSize: 24,
  fill: 0xff1010,
  align: "center"
});
// burnTimerText.anchor.x = 0.5;
burnTimerText.y = 70;
burnTimerText.x = STAGE_WIDTH;



// load the texture we need
const createPlayer = resources => {
  const p = new PIXI.Sprite(resources.blizzard.texture);
  p.width = playerWidth;
  p.height = playerHeight;

  // Setup the position of the player
  p.x = STAGE_WIDTH / 2;
  p.y = app.renderer.height / 2;

  p.interactive = true;
  p.hitArea = new PIXI.Rectangle(0, 0, playerWidth, playerHeight);

  const fire = new PIXI.Sprite(resources.playerFire.texture);
  fire.alpha = 0;
  fire.name = 'fire';
  p.addChild(fire);


  return p;
};
// const createPlayerFire = resources => {
//
// }

const createWaterZones = () => {
  const zones = [];


  for (let zoneLocation of waterZoneLocations) {
    const zone = new PIXI.Graphics();
    zone.beginFill(0x3498db); // Blue color
    zone.drawRect(0, 0, waterZoneSize, waterZoneSize);
    zone.endFill();
    zone.alpha = 0;
    zone.x = zoneLocation.x;
    zone.y = zoneLocation.y;
    zone.interactive = true;
    zone.hitArea = new PIXI.Rectangle(0, 0, waterZoneSize, waterZoneSize);

    zones.push(zone);
  }

  return zones;
};

const createBurnBar = () => {
  const burnBar = new PIXI.Graphics();
  burnBar.lineStyle(1, 0xffffff);
  burnBar.lineTo(199, 0);
  burnBar.lineTo(199, 40);
  burnBar.lineTo(0, 40);
  burnBar.lineTo(0, 0);
  burnBar.y = 110;
  burnBar.x = STAGE_WIDTH;
  burnBar.zIndex = 999;

  const burnBarFill = new PIXI.Graphics();
  burnBarFill.beginFill(0xff0000); // Blue color
  burnBarFill.drawRect(1, 1, burnValue, 38);
  burnBarFill.endFill();
  burnBarFill.name = 'fill';
  burnBar.addChild(burnBarFill);


  return burnBar;
};

app.loader
  .add("map", "assets/map.png")
  .add("riot", "data:image/png;base64,")
  .add("blizzard", "data:image/png;base64,")
  .add("playerFire", "data:image/png;base64,")
  .add("poster1", "data:image/png;base64,")
  .add("poster2", "data:image/png;base64,")
  .add("poster3", "data:image/png;base64,")
  .load((loader, resources) => {

    // This creates a texture from a 'player.png' image
    const background = new PIXI.Sprite(resources.map.texture);
    background.width = STAGE_WIDTH;
    background.height = STAGE_HEIGHT;
    app.stage.addChild(background);

    const player = createPlayer(resources);
    // const playerFire = createPlayerFire(resources);
    const zones = createWaterZones();
    const burnBar = createBurnBar();

    const onKeyDown = key => {
      // p Key is 80
      if (key.keyCode === 80) {
        if (pauseState) {
          // will be set to false
          posterScoreText.text = `Posters Burned: ${posterScore}`;
        } else {
          // will be set to true
          posterScoreText.text = `GAME PAUSED \n press p to continue...`;
        }
        pauseState = !pauseState;
      }

      if (pauseState) {
        return;
      }

      // W Key is 87
      // Up arrow is 87
      if (key.keyCode === 87 || key.keyCode === 38) {
        // If the W key or the Up arrow is pressed, move the player up.
        if (player.position.y - playerHeight >= 0) {
          // Don't move up if the player is at the top of the stage
          player.position.y -= playerHeight;
        }
      }

      // S Key is 83
      // Down arrow is 40
      if (key.keyCode === 83 || key.keyCode === 40) {
        // If the S key or the Down arrow is pressed, move the player down.
        if (player.position.y != app.renderer.height - playerHeight) {
          // Don't move down if the player is at the bottom of the stage
          player.position.y += playerHeight;
        }
      }

      // A Key is 65
      // Left arrow is 37
      if (key.keyCode === 65 || key.keyCode === 37) {
        // If the A key or the Left arrow is pressed, move the player to the left.
        if (player.position.x != 0) {
          // Don't move to the left if the player is at the left side of the stage
          player.position.x -= playerWidth;
        }
      }

      // D Key is 68
      // Right arrow is 39
      if (key.keyCode === 68 || key.keyCode === 39) {
        // If the D key or the Right arrow is pressed, move the player to the right.
        if (player.position.x < STAGE_WIDTH - playerWidth) {
          // Don't move to the right if the player is at the right side of the stage
          player.position.x += playerWidth;
        }
      }

      checkPlayerPosition();
      // console.log({ x: player.position.x, y: player.position.y });
    };

    const spawnPoster = () => {
      // if max amount of spawned posters is reached, skip
      if (posters.length >= maxSpawnedPosters) return;

      // Spawns the posters at random positions on our stage
      const posterTextures = [
        resources.poster1.texture,
        resources.poster2.texture,
        resources.poster3.texture
      ];
      const poster = new PIXI.Sprite(posterTextures[_.random(2)]);

      // Set the position of our target
      const { x, y } = generateRandomPosition();
      poster.width = playerWidth;
      poster.height = playerHeight;
      poster.position.x = x;
      poster.position.y = y;
      poster.interactive = true;
      poster.buttonMode = true;
      poster.on('click', function () {
        debugger;
      });
      // poster.rotation = 135;
      posters.push(poster);
      app.stage.addChild(poster);
      // console.log(posters);
    };

    const generateRandomPosition = () => {
      // Create two random points on our stage

      const randomX = _.random(19);
      // const randomX = Math.floor(Math.random() * resolutionMultiplier);
      const randomY = _.random(19);

      console.log(randomY);
      const x = playerWidth * randomX;
      const y = playerHeight * randomY;

      // ensure it does not spawn on player
      if (player.position.x === x && player.position.y === y)
        return generateRandomPosition();


      if (zones.some(zone => zone.x <= x && x <= zone.x + zone.width &&
          zone.y <= y && y <= zone.y + zone.height))
        return generateRandomPosition();

      // ensure it does not spawn on other posters
      let willOverlap =
        posters.some(
          poster => poster.position.x === x && poster.position.y === y
        ) ||
        // ensure it does not spawn on water tiles
        zones.some(
          zone =>
            zone.x <= x &&
            x <= zone.x + waterZoneSize &&
            zone.y <= y &&
            y <= zone.y + waterZoneSize
        );

      if (willOverlap) {
        // console.log("Would've overlapped.");
        return generateRandomPosition();
      }

      return { x, y };
    };

    const checkPlayerPosition = () => {
      // If the player and target are at the same position, spawn the target in another position

      // check for poster collision
      for (let i = 0; i < posters.length; i++) {
        const poster = posters[i];
        if (
          poster.position.x === player.position.x &&
          poster.position.y === player.position.y
        ) {
          // Destroy poster
          poster.destroy();
          posters.splice(i, 1);
          // Increase score
          incrementScore();
          break;
        }
      }

      // check for player in water
      inWater = zones.some(zone => hitTestRectangle(player, zone))
    };

    const posterSpawnManager = () => {
      // logic to increase
      // the number of posters that spawn OR
      // the frequency that they spawn
      const elapsedTime = startTime.diffNow('seconds');
      if (elapsedTime > 10) {
        posterSpawnInterval = 5000;
        if (elapsedTime > 30) {
          posterSpawnInterval = 4000;
        }
        if (elapsedTime > 50) {
          posterSpawnInterval = 3000;
        }
        if (elapsedTime > 100) {
          posterSpawnInterval = 2000;
        }
      }

      // if pause state is false
      if (!pauseState) {
        // spawn x amount of posters at one time - posterSpawnAmount
        for (let s = 0; s < posterSpawnAmount; s++) {
          spawnPoster();
        }
      }

      // set interval for respawning - posterSpawnInterval
      setTimeout(function() {
        posterSpawnManager();
      }, posterSpawnInterval);
    };

    // Event Listeners
    document.addEventListener("keydown", onKeyDown);
    // document.addEventListener("mousedown", spawnPoster);
    posterSpawnManager();

    // Add the player to the scene we are building
    app.stage.addChild(...zones);
    app.stage.addChild(posterScoreText);
    app.stage.addChild(protestInfluenceText);
    app.stage.addChild(burnBar);
    app.stage.addChild(player);

    window.player = player;
    // console.log(zones);
    // Listen for frame updates
    window.Ticker = app.ticker;
    app.ticker.add(() => {
      // each frame we spin the player around a bit
      // player.rotation += 0.01;

      if (!pauseState) {
        const playerFire = player.getChildByName('fire');

        if (inWater) {
          // player is in water
          if (playerFire.alpha > 0) playerFire.alpha -= 0.1;
        } else {
          if (playerFire.alpha < 0.8) playerFire.alpha += 0.05;
        }

        throttleBurnValueHandler();

        // console.log(playerFire.alpha );

        // console.log(collisionTest);
        throttledInfluenceCounter();
      }
    });

    const throttleBurnValueHandler = _.throttle(function () {
      const fill = burnBar.getChildByName('fill');
      fill.clear();
      fill.beginFill(0xff0000); // Blue color
      if (inWater) {
        if (burnValue < burnValueMax) burnValue += 5
      } else {
        if (burnValue > 0) burnValue -= 1;
      }
      fill.drawRect(1, 1, burnValue, 38);
      fill.endFill();

    }, 200);

  });

const throttledInfluenceCounter = _.throttle(function () {
  // increase protest influence by each poster per tick
  protestInfluence += posters.length;
  protestInfluenceText.text = `Influence: ${protestInfluence}`;
}, 1000);
const incrementScore = () => {
  posterScore++;
  posterScoreText.text = `Posters Burned: ${posterScore}`;
};

const hitTestRectangle = (r1, r2) => {

  //Define the variables we'll need to calculate
  let combinedHalfWidths, combinedHalfHeights, vx, vy;

  //hit will determine whether there's a collision
  //Find the center points of each sprite
  r1.centerX = r1.x + r1.width / 2;
  r1.centerY = r1.y + r1.height / 2;
  r2.centerX = r2.x + r2.width / 2;
  r2.centerY = r2.y + r2.height / 2;

  //Find the half-widths and half-heights of each sprite
  r1.halfWidth = r1.width / 2;
  r1.halfHeight = r1.height / 2;
  r2.halfWidth = r2.width / 2;
  r2.halfHeight = r2.height / 2;

  //Calculate the distance vector between the sprites
  vx = r1.centerX - r2.centerX;
  vy = r1.centerY - r2.centerY;

  //Figure out the combined half-widths and half-heights
  combinedHalfWidths = r1.halfWidth + r2.halfWidth;
  combinedHalfHeights = r1.halfHeight + r2.halfHeight;

  //Check for a collision on the x axis
  //`hit` will be either `true` or `false`
  return Math.abs(vx) < combinedHalfWidths ? Math.abs(vy) < combinedHalfHeights : false;
};
